Triggers.Trigger:

var name=getHeadValue("jixing");
var mingcheng=getHeadValue("shangpinmingcheng");
if(name==0){
   alert("请填写商品剂型信息");
    return;
}
if(mingcheng==''){
   alrt("请填写商品名称信息");
    return;
}
if(getHeadValue("xiaoqidanwei")==0){
   alrt("请填写效期单位信息");
    return;
}
if(getHeadValue("danwei")==0){
   alrt("请填写剂型信息");
    return;
}
if(getHeadValue("jixing")==0){
   alrt("请填写单位信息");
    return;
}
  var v=getHeadValue("shangpinleibie");
     if(v>=1&&v<=7){
        if(getHeadValue("chufangfenlei")==0){
        alert("请填写处方分类信息");
        return;
     }
}

  if(getStatus()==0){
    var orderFunc=@save.orderFunc('shangpinjixing',name,mingcheng);
    var zhujimaFunc=@save.zhujimaFunc(mingcheng);
    Promise.all([orderFunc,zhujimaFunc]).then(function(data) {
             var order=JSON.parse(data[0]);
             var zhu=JSON.parse(data[1]);
              if(order.result[0].bianhao){
                var bianhao=order.result[0].bianhao;
                setHeadValue("shangpinbianhao",bianhao);
                setHeadValue("goodsid",bianhao);
             }
             if(zhu.result[0].zhujima){
                var zhujima=zhu.result[0].zhujima;
                setHeadValue("zhujima",zhujima);
             }
             setStatus(1);
             @$this.save();
            } ,function(e){
        });
    }else{
        if(getStatus()==20){
            alert("单据已经提交，不能编辑");
        }else{
             @$this.save();
        }
    }


