    if(getStatus()==0){
    var orderFunc=@save.orderFunc('shangpinjixing',name,mingcheng);
    var zhujimaFunc=@save.zhujimaFunc(mingcheng);
    Promise.all([orderFunc,zhujimaFunc]).then(function(data) {
             var order=JSON.parse(data[0]);
             var zhu=JSON.parse(data[1]);
            if(order.result[0].bianhao){
                var bianhao=order.result[0].bianhao;
                setHeadValue("shangpinbianhao",bianhao);
                setHeadValue("goodsid",bianhao);
             }
             if(zhu.result[0].zhujima){
                var zhujima=zhu.result[0].zhujima;
                setHeadValue("zhujima",zhujima);
             }
            @$this.save('bills',{
             before:{
                setStatus:[20]
             }
             });
            } ,function(e){
        });
    }else{
        if(getStatus()==20){
            alert("单据已经提交，不能编辑");
          }else{
             @$this.save('bills',{
             before:{
                setStatus:[20]
             }
             });
          }
    }

BillUIWidget.Init:

		var value = getHeadValue('kaipiaoriqi');
		if(value == '' || !value){
			var t=this.formatDate(new Date());
        	this.setValue(t);
		}
		var kaipiaoyuan = getHeadValue('kaipiaoyuan');
		if(kaipiaoyuan == '' || !kaipiaoyuan){
			var t=this.getRealName();
			setHeadValue('kaipiaoyuan',t);
		}


    var v=getHeadValue("shangpinleibie");
     if(v>=1&&v<=7){
        setShow("two");
        setHide("four");
        setHide("three");
    }else if(v>=15){
        setShow("three");
        setHide("two");
        setHide("four");
    }else{
        setShow("four");
        setHide("two");
        setHide("three");
    }
this.loadDataSource();

Triggers.Trigger:

var v=this.getValue();
    if(v>=1&&v<=7){
        setShow("two");
        setHide("four");
        setHide("three");
    }else if(v>=15){
        setShow("three");
        setHide("two");
        setHide("four");
    }else{
        setShow("four");
        setHide("two");
        setHide("three");
    }
